INSERT INTO `jos_content` (`id`, `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `article_image`) VALUES
(4, 'GUINOT verdens nr.1 indenfor skønhedspleje', 'ny-guinot-afdeling-pa-jydsk-lasercenter-verdens-nr1-indenfor-skonhedspleje', '', '<img src="images/rokquickcart/01_agelogicetyeux.jpg" border="0" width="160" height="214" /><img src="images/rokquickcart/06_gammelonguevie.jpg" border="0" width="153" height="245" /><img src="images/rokquickcart/face clean guinot.jpg" border="0" width="177" height="176" /><img src="images/rokquickcart/02_ambiancecremespot.jpg" border="0" width="144" height="193" /><br /><br /><strong><br /></strong>\r\n<h1><strong>GUINOT</strong></h1>\r\n<h2>Med mere end 30 års erfaring indenfor Bio Science og professionel hudpleje på skønhedsklinikker og kosmetologskoler, er Guinot''s plejemetoder og formler blandt de førende på verdensmarkedet og i dag nr. 1 i både Frankrig, Storbritanien og USA. Guinot''s videnskabelige program udvikles i samarbejde med biokemikere og dermatologer og den omfattende hudplejeserie er plantebaseret og fremstilles uden parabener. Produkterne testes på hospitalet Cochin i St. Louis udenfor Paris.</h2>\r\n<h2><br />Her på klinikken kan du vælge imellem et særligt håndplukket udvalg af hudplejeserien og få <span style="text-decoration: underline;"><strong>gratis prøvetuber</strong></span> med hjem til afprøvning, før du beslutter dig. Du kan også købe de fleste af produkterne fra hudplejeserien via webshoppen <a href="www.hudlaegenscremeshop.dk" target="_blank">www.hudlaegenscremeshop.dk<span style="text-decoration: underline;"> </span></a>og få gratis prøvetuber med i ordren.</h2>\r\n<h2><img src="images/rokquickcart/08_matizone 600x800.jpg" border="0" width="186" height="248" /> <img src="images/rokquickcart/15_corpspackrouge.jpg" border="0" width="201" height="263" /> <img src="images/rokquickcart/02_hommeproduits.jpg" border="0" width="155" height="211" /></h2>\r\n<br /><br /><br />\r\n<h1>GUINOT Kundefordele på klinikken</h1>\r\n<h2>På Jydsk Lasercenter kan vi tilbyde en rabat-ordning på vores håndplukkede Guinot sortiment. Ved køb af 10 Guinot produkter får du 50% rabat på dit næste Guinot produkt.</h2>\r\n<h2>Guinot Bonus-kortet kan du få ved henvendelse i receptionen. Husk også at du kan få <strong>gratis prøvetuber eller prøvebægre</strong> samt kataloget over den omfattende hudplejeserie med hjem.</h2>\r\n<br /><img src="images/rokquickcart/bonuskort .jpg" border="0" width="209" height="257" /> <img src="images/rokquickcart/echantillons3.jpg" border="0" width="286" height="249" /> <img src="images/rokquickcart/11_texturescreme.jpg" border="0" width="326" height="217" /> <br />', '', 1, 0, 0, 0, '2010-03-03 08:18:35', 62, '', '2012-11-07 09:18:49', 62, 0, '0000-00-00 00:00:00', '2010-03-03 08:18:35', '0000-00-00 00:00:00', '', '', 'item_url=', 40, 0, 9, 'Guinot, hudpleje, hudplejeserie, skønhedsbehandling, Hydradermie, Aromatic, Liftosome, Beaute Neuve', '', 0, 6972, 'robots=\nauthor=', 'images/rokquickcart/guinot ny.jpg'),
(5, 'Kontakt 1', 'kontakt-1', '', '<h1><br /><br /></h1>\r\n<h2>Henvendelse til klinikken kan ske indenfor de anførte åbningstider eller ved at udfylde nedenstående kontaktformular. Vi beder dig venligst oplyse dit <strong>cpr.nr</strong>, så vi kan behandle din forespørgsel så hurtigt og effektivt som muligt.<br /><br /><br /></h2>\r\n__________________________________________________<br /><br />\r\n<h2><strong>Fri adgang for lyspatienter i behandling:</strong></h2>\r\n<h2><strong>Mandag - fredag kl. 08:00 - 15:30</strong></h2>\r\n<strong><br />___________________________________________<br /><br /></strong>\r\n<h2><span style="text-decoration: underline;"><strong><br />Klinikkens åbningstider </strong></span></h2>\r\n<h2><br /><br /><strong>Mandag - fredag kl.  08:00 - 18:00<br /><br />Fredage ulige uger: 08:00 - 16:00<br /><br /><br /><br /><span style="text-decoration: underline;">Pr. 1. september 2013<br /><br /></span>Alle mandage - lige uger:    LUKKET</strong></h2>\r\n<h2><br /><br />Konsultationer og behandlinger hos speciallæge Uffe Gjede <strong>efter kl.15:00</strong> kræver <strong>særlig aftale</strong> m. speciallægen. Henvendelse herom via receptionen eller til laser@jydsklasercenter.dk  (kontaktformularen).</h2>\r\n<h2><br /><strong><br /><br />EFTERÅRSFERIE LUKKET:  UGE 41 <br /><br /><br /></strong><br /><br /><br /><br /></h2>', '', 1, 0, 0, 0, '2010-03-03 08:31:43', 62, '', '2013-08-07 06:37:54', 62, 0, '0000-00-00 00:00:00', '2010-03-03 08:31:43', '0000-00-00 00:00:00', '', '', 'item_url=', 89, 0, 8, '', '', 0, 0, 'robots=\nauthor=', ''),
(6, 'Kontakt 2', 'kontakt-2', '', '<ul class="list_2">\r\n<li>Jydsk Lasercenter<br />Havnetorv 1b<br />7700 Thisted<br />Tlf : 97926700</li>\r\n<li><a href="mailto:laser@jydsklasercenter.dk">laser@jydsklasercenter.dk</a> </li>\r\n</ul>', '', 1, 0, 0, 0, '2010-03-03 08:32:42', 62, '', '2013-08-13 13:46:27', 62, 0, '0000-00-00 00:00:00', '2010-03-03 08:32:42', '0000-00-00 00:00:00', '', '', 'item_url=', 4, 0, 7, '', '', 0, 0, 'robots=\nauthor=', ''),
(7, 'Kvittering', 'kvittering', '', '<h4>Tak for din henvendelse!</h4>\r\n<h4>Da vi modtager mange patient henvendelser dagligt, kan der forekomme ekspeditionstid på op til 5 hverdage. <br /><br /> Med venlig hilsen<br /> Jydsk Lasercenter</h4>', '', 1, 0, 0, 0, '2010-03-03 08:52:38', 62, '', '2012-06-20 09:01:52', 62, 62, '2013-06-25 13:07:53', '2010-03-03 08:52:38', '0000-00-00 00:00:00', '', '', 'item_url=', 3, 0, 6, '', '', 0, 0, 'robots=\nauthor=', ''),
(9, 'Velkommen til Jydsk Lasercenter', 'velkommen-til', '', '<br />\r\n<h4>Jydsk Lasercenter er i dag Skandinaviens førende indenfor laserbehandling af hudsygdomme og kosmetiske hudforandringer samt internationalt uddannelsescenter for speciallæger i laserterapi. Vores avancerede laserudstyr og hudplejeprodukter og mange årige erfaring er din garanti for tryghed og kvalitet. Vi kan følge din huds sundhedstilstand med ultralydscanning før og efter en behandling og vejleder dig i, hvordan du opnår en optimal hudpleje.</h4>\r\n<p> </p>\r\n<p> </p>\r\n<p> </p>\r\n<p> </p>\r\n<p> </p>\r\n<h4><span style="text-decoration: underline;">Professionelle skønhedsbehandlinger og kosmetiske injektioner</span></h4>\r\n<p> </p>\r\n<h4>Vi kan også hjælpe dig med at vedligeholde et ungdommeligt udseende med Juvéderm og Botox Vistabel behandlinger eller med Guinot''s verdenskendte hudplejeprodukter, som testes på hospitalet St. Louis udenfor Paris. Kosmetiske injektioner tilpasses individuelt og i forhold til lige netop din hudtype.</h4>\r\n<br /><br /><br /><br /><br /><br />\r\n<h4><span style="text-decoration: underline;">Unikum i laserterapi</span></h4>\r\n<p> </p>\r\n<h4>Hos Jydsk Lasercenter tænker vi stort og bredt med exceptionel ekspertise. Det kan vi, fordi vi som den eneste laserklinik i Skandinavien råder over den største og mest avancerede behandlingspalette indenfor laserterapi. Det giver dig mulighed for at opnå et optimalt individuelt resultat. På Jydsk Lasercenter er kvalitet og tryghed en selvfølge og vi bestræber os hele tiden på at skabe enestående resultater. På laserklinikken har vi i alt 11 forskellige laser systemer, som selekteres ud til individuel behandling og samtidig kan kombineres med aktive hudplejeprodukter, Juvéderm eller Botox Vistabel behandlinger, rødt LED-lys og Aluma Skin Tightening. Ofte anvendes ikke bare forskellige laser systemer men også forskellige laser apparater under et behandlingsforløb.</h4>\r\n<h4>Vi anbefaler dig at starte med en konsultation, hvor dine behov og ønsker evalueres i forhold til de praktiske muligheder. Laser behandlingen sammensættes altid på den mest skånsomme måde af speciallægen.</h4>\r\n<br /><br /><img src="images/rokquickcart/Behandlinger/beautydoctor.jpg" border="0" width="392" height="278" /><img src="images/rokquickcart/Behandlinger/aging.jpg" border="0" width="180" height="290" /><br /><br /><br />\r\n<h4><strong><span style="text-decoration: underline;">Vores specialer</span></strong></h4>\r\n<br />\r\n<h4>På klinikken anvender vi 11 avancerede lasersystemer:</h4>\r\n<br />\r\n<h4><em><strong>Fractional CO2 DOT laser</strong> - </em>til behandling af rynker, solskadet og/eller ældet hud samt akne ar - erstatter kirurgi på øvre og nedre øjenlåg og kan udføres på kun 10 minutter!</h4>\r\n<h4><br /><em><strong>Lumenis m. IPL og langpulset ND Yag</strong> - </em>til behandling af karsprængninger og rosacea i ansigt samt karsprængninger på hals og bryst. Kan også indstilles til fjernelse af pigmentforskydninger (Hyperpigmentatio).</h4>\r\n<h4><br /><em><strong>Lumenis One laser</strong> - </em>til behandling af karsprængninger på ben. Denne laser regnes for at være verdens absolut bedste til en række af forskellige laserbehandlinger og er på Jydsk Lasercenter den eneste at finde i Danmark.</h4>\r\n<h4><br /><em><strong>Beauty Star laser</strong> - </em>til behandling af selektive former for røde kar og pigmentpletter i ansigtet</h4>\r\n<h4><br /><strong><em>Langpulset Farvestoflaser (PDL)</em></strong> - Til fjernelse af visse typer af kar og røde modermærker (naevus flammeus)</h4>\r\n<h4><br /><em><strong>Q-switchet TattooStar laser</strong> -</em> til fjernelse af tatoveringer, permanent makeup og pigmenterede hudområder. Denne laser er den mest avancerede tattoo laser på markedet og hos Jydsk Lasercenter den eneste i hele Skandinavien.</h4>\r\n<h4><br /><em><strong>Synchro HP laser</strong> med IPL og langpulset ND Yag - </em>til behandling af karsprængninger generelt inklusiv på ben og hårepilering, hemangiomer (rubinpletter)</h4>\r\n<h4><br /><em><strong>Aluma Skintightning</strong> -</em> til opstramning af slap hud i ansigt og på hals</h4>\r\n<h4><br /><em><strong>Erbium Yag-laser og Co2-laser</strong></em> - til behandling af rynker, ardannelser samt fjernelse af diverse hudtumores</h4>\r\n<h4><br /><strong><em>Cool Touch laser</em> </strong>- til hudforbedring af især solskadet og ældet hud med reduktion af helt fine rynkelinjer i huden samt nedsunkne ardannelser</h4>\r\n<h4><br /><em><strong>Mediostarlaser XT</strong></em> - til permanent hårfjerning</h4>\r\n<img src="images/rokquickcart/lumenis-logo.png" border="0" width="251" height="102" /><img src="images/rokquickcart/deka-logo.jpg" border="0" width="201" height="97" /> <img src="images/rokquickcart/logo_asclepion_01.gif" border="0" width="199" height="199" /> <img src="images/rokquickcart/dotlogo.jpg" border="0" /> <img src="images/rokquickcart/cooltouchlogo.jpg" border="0" width="264" height="66" /><img src="images/rokquickcart/logo - photocure.jpg" border="0" width="174" height="171" /><img src="images/rokquickcart/lumenisone logo.jpg" border="0" width="341" height="116" /> <img src="images/rokquickcart/logo-synchro-multi-platform-laser.jpg" border="0" width="241" height="74" /><img src="images/rokquickcart/alumalogo.jpg" border="0" width="297" height="101" /><br /><br /><br /><img src="images/rokquickcart/juvederm-logo2.jpg" border="0" width="315" height="78" /> <img src="images/rokquickcart/Behandlinger/vistabel_logo_b2.jpg" border="0" width="231" height="231" /><img src="images/rokquickcart/Behandlinger/guinot.jpg" border="0" width="296" height="113" /><br /><br /><br /><br /><br /><br /><img src="images/rokquickcart/bplogo.png" border="0" /> <img src="images/rokquickcart/derma_logo_jpeg1.jpg" border="0" width="265" height="165" /><br /><br /><br /> <br /><br /><br />', '', 1, 0, 0, 0, '2010-03-03 10:10:06', 62, '', '2013-11-20 11:17:20', 62, 62, '2014-01-21 07:04:2